import MenuLogin from "../components/MenuLogin";
import IconeApple from "../assets/images/apple.png";
import IconeFacebook from "../assets/images/face.png";
import IconeGoogle from "../assets/images/Google.png";
import React from "react";


function Index() {
    return (
        <>
            <div class="showcase">

                <div class="conteudo" id="conteudo">
                    <div class="blur"></div>
                    <div class="imagem-principal">

                        <h3>
                            <p>Entre na sua conta e</p> divulgue seus eventos.
                        </h3>
                    </div>
                    <div class="credencial">
                        <input type="text" placeholder="Usuário ou email" required></input>
                        <br></br>
                        <input type="text" placeholder="Senha" required></input>
                        <p class="texto-mostrarSenha">Mostrar a senha</p>
                        <br></br>
                        <button>Login</button>
                        <p class="texto-maisOpcoes">ou continue com</p>
                        <div class="maisOpcoes">
                            <div class="opcaoApple"><img src={IconeApple} alt="" width="40%"></img></div>
                            <div class="opcaoGoogle"><img src={IconeGoogle} alt="" width="40%"></img></div>
                            <div class="opcaoFace"><img src={IconeFacebook} alt="" width="20%"></img></div>
                        </div>
                        <h4 class="texto-esqueceuSenha"><a href="/#">Esqueceu sua senha?</a></h4>
                    </div>

                </div>
                </div>

        </>

    );
};

export default Index;
