import React from "react";
import Api from '../components/Axios';
function Index() {



    var locations = []
    var google = new google.maps.Map(document.getElementById("mapa"))

    function buscarEventosProximos() {

        let params = {
            latitute: -23.5791213,
            longitute: -46.6658027
        }
        Api.post("evento/eventos-proximos", params).then(function (resultado) {
            console.log(resultado.data.length);
            for (let i = 0; i < resultado.data.length; i++) {
                locations[i] = new Array(resultado.data.length)
                for (let j = 0; j < locations.length; j++) {
                    locations[i][0] = resultado.data[i][0]
                    locations[i][1] = resultado.data[i][3]
                    locations[i][2] = resultado.data[i][4]

                }
            }
            // eventosProximos = resultado.data[0]
        }).catch(e => {
            console.log(e);
        })

        console.log(locations)

    }



    function initialize() {
        var mapOptions = {
            center: { lat: -23.5791213, lng: -46.6658027 },
            zoom: 15
        };
        google = new google.maps.Map(document.getElementById("mapa"), mapOptions);
        locations = buscarEventosProximos()

        setTimeout(() => {
            for (var i = 0; i < locations.length; i++) {
                var marker = new google.maps.Marker({
                    position: new google.maps.LatLng(locations[i][1], locations[i][2]),
                    title: locations[i][0],
                    map: google
                });
            }
        }, 1000);
        
    }

    google.maps.event.addDomListener(window, 'load', initialize);



    <script async defer
        src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBvroKF-1rTRd9K-L9P3ILzSCjcFLU1LkQ&callback=initialize">
    </script>

    return (
        <>
            <div id="mapa" onLoad={buscarEventosProximos()}></div>
        </>
    );
}

export default Index
