import React from "react";
import "../assets/css/all.css";
import MenuLogin from "../components/MenuLogin";
// import MenuLogin from "../components/MenuLogin";


function Index() {
    return (
        <div className="showcase">
            <MenuLogin />

            <div className="conteudo" id="conteudo">
                <div className="blur"></div>
                <div className="imagem-principal">

                    <h3>
                        <p>Entre na sua conta e</p> divulgue seus eventos.
                    </h3>
                </div>
                <div className="credencial">
                    <input type="text" placeholder="Usuário ou email" required></input>
                    <br></br>
                    <input type="text" placeholder="Senha" required></input>
                    <p className="texto-mostrarSenha">Mostrar a senha</p>
                    <br></br>
                    <button>Login</button>
                    <p className="texto-maisOpcoes">ou continue com</p>
                    <div className="maisOpcoes">
                        <div className="opcaoApple"><img src="Images/Login/apple.png" alt="" width="40%"></img></div>
                        <div className="opcaoGoogle"><img src="Images/Login/Google.png" alt="" width="40%"></img></div>
                        <div className="opcaoFace"><img src="Images/Login/face.png" alt="" width="20%"></img></div>
                    </div>
                    <h4 className="texto-esqueceuSenha"><a href="/#">Esqueceu sua senha?</a></h4>
                </div>

            </div>

        </div>

    );
};

export default Index;
