import "../assets/css/react.css";
import Seta from "../assets/images/seta.png";
import Logo from "../assets/images/logo.png";
import Wifi from "../assets/images/wifi.png";
import Bg from "../assets/images/bg-color.png"
import Btn from "../components/BtnEsqueceuSenha";

function Index() {

  const history = useHistory()

  const [codigo, setCodigo] = useState("")
  // const [error, setError] = useState("")

  const handleEnviarCodigo = useCallback(
    async (dados) => {
      dados.preventDefault()
      if (email == "") {
        alert("Preencha o código enviado pelo email")
      } else {
        try {
          let params = {

            codigo: codigo

          }
          const response = await Api.post("/usuarios/validaCodigo", params)
          if (response.status == 200) {
            // const usuarioString = JSON.stringify(response.data)
            // localStorage.setItem("usuario", usuarioString)
            // const objetoEmFormatoDeString = localStorage.getItem('usuario');
            // const objetoMesmo = JSON.parse(objetoEmFormatoDeString)
            history.push("/alterar-senha")
          } else {
            alert("Insira o código correto!")
          }
        } catch (erro) {
          alert("teste");
        }
      }
    }, [email, history])

  return (
    <div className="content">
      <img src={Bg} alt="" className="bg-color" />
      <div className="meio">
        <div className="header">
          <img src={Seta} alt="" />
          <h1 className="text-voltar">Voltar</h1>
        </div>

        <img src={Logo} alt="" className="logo" />
        <img src={Wifi} alt="" className="wifi" />
        <h1 className="texto">Insira o código que te enviamos</h1>
        <form  onSubmit={handleEnviarCodigo}></form>
        <input type="text" placeholder="Código" className="input" />
        <button className="confirmar" type="submit">Confirmar</button>
        <div className="reenviar">
          Não recebeu o código? <u>Reenviar</u>
        </div>
      </div>
    </div>
  );
}

export default Index;
