import seta from "../assets/images/seta.png";
import bg from "../assets/images/bg-color.png";
import Logo from "../assets/images/logo.png";
import "../assets/css/react.css";
import Btn from "../components/BtnEsqueceuSenha";

function Index() {
  alert(sessionStorage.id_usuario)

  const [senha, setSenha] = useState("")
  // const [error, setError] = useState("")

  const handleValidarCodigo = useCallback(
    async (dados) => {
      dados.preventDefault()
      if (codigoRecuperaSenha === "") {
        alert("Preencha o código enviado pelo email")
      } else {
        try {
          let params = {

            senha: senha

          }
          const response = await Api.post(`/usuarios/atualizarDadosConta/${sessionStorage.id_usuario}`, params)
          if (response.status === 200) {
            const id = JSON.stringify(response.data)
            sessionStorage.id_usuario = id;

            history.push("/alterar-senha")
          } else {
            alert("Insira o código correto!")
          }
        } catch (erro) {
          alert(erro);
        }
      }
    }, [senha, history])
  return (
    <div className="content">
      <img src={bg} alt="" className="bg-color" />
      <div className="meio">
        <div className="header">
          <img src={seta} alt="" className="seta" />
          <h1 className="text-voltar">Voltar</h1>
        </div>
        <img src={Logo} alt="" className="logo" />
        <h1 className="texto">Altere sua senha:</h1>
        <input type="text" placeholder="Senha" className="input" />
        <input type="text" placeholder="Confirme sua senha" className="input" onChange={dados => setCodigoRecuperaSenha(dados.target.value)} />
        <button className="confirmar" texto="Alterar" destino="/index/">Confirmar</button>
      </div>
    </div>
  );
}

export default Index;
