import React from "react";
import Api from '../components/Axios';
function Index() {



    var locations = []

    var map

    function buscarEventosProximos() {

        let params = {
            latitute: -23.5791213,
            longitute: -46.6658027
        }
        Api.post("evento/eventos-proximos", params).then(function (resultado) {
            console.log(resultado.data.length);
            for (let i = 0; i < resultado.data.length; i++) {
                locations[i] = new Array(resultado.data.length)
                for (let j = 0; j < locations.length; j++) {
                    locations[i][0] = resultado.data[i][0]
                    locations[i][1] = resultado.data[i][3]
                    locations[i][2] = resultado.data[i][4]

                }
            }
            // eventosProximos = resultado.data[0]
        }).catch(e => {
            console.log(e);
        })

        console.log(locations)

    }


    // Create the script tag, set the appropriate attributes
var script = document.createElement('script');
script.src = 'https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&callback=initMap';
script.async = true;

// Attach your callback function to the `window` object
window.initMap = function() {
  // JS API is loaded and available
};

// Append the 'script' element to 'head'
document.head.appendChild(script);
    function buscarMapa() {



        var coordenadas = { lat: -23.5791213, lng: -46.6658027 };
        var zoomLevel = 12;
        function initialize() {
            var mapOptions = {
                center: coordenadas,
                zoom: 15
            };
            map = new google.maps.Map(document.getElementById("mapa"), mapOptions);
            locations = buscarEventosProximos()

            setTimeout(() => {
                for (var i = 0; i < locations.length; i++) {
                    var marker = new google.maps.Marker({
                        position: new google.maps.LatLng(locations[i][1], locations[i][2]),
                        title: locations[i][0],
                        map: map
                    });
                }
            }, 1000);

        }

        google.maps.event.addDomListener(window, 'load', initialize);



        <script async defer
            src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBvroKF-1rTRd9K-L9P3ILzSCjcFLU1LkQ&callback=initialize">
        </script>
    }
    return (
        <>
            <div id="mapa" onLoad={buscarEventosProximos(), buscarMapa()}></div>
        </>
    );
}

export default Index
